/**
 * I am aware of lodash.pick, but I am not sure if I want to install entire lodash into falcon client
 * @param {object} from
 * @param {string[]} props
 */
export function pick(from, props = []) {
  return Object.entries(from)
    .filter(([name]) => props.some(x => x === name))
    .reduce((result, [name, value]) => ({ ...result, [name]: value }), {});
}
