/**
 * Set proper Cache-Control headers for SSR rendered content
 * @returns {function(ctx: object, next: function): Promise<void>} Koa middleware
 */
export default () => {
  return async (ctx, next) => {
    await next();
    ctx.set('Cache-Control', 'no-store, no-cache');
    ctx.set('Pragma', 'no-cache');
    ctx.set('Expires', 0);
  };
};
