function getPossibleTypes(fragmentMatcherData) {
  // eslint-disable-next-line no-underscore-dangle
  return fragmentMatcherData.__schema.types.reduce((result, type) => {
    return {
      ...result,
      [type.name]: (type.possibleTypes || []).map(subtype => subtype.name)
    };
  }, {});
}

export { getPossibleTypes };
