import { themed } from '../theme';

export const Select = themed({
  tag: 'select',

  defaultProps: {
    invalid: false
  },

  defaultTheme: {
    select: {
      py: 'xs',
      px: 'sm',
      border: 'regular',
      borderRadius: 'medium',

      css: ({ theme, invalid }) => ({
        display: 'block',
        fontFamily: 'inherit',
        lineHeight: 'inherit',
        color: 'inherit',
        borderColor: invalid ? theme.colors.error : theme.colors.secondaryDark,
        width: '100%',
        position: 'relative',
        background: `url(data:image/svg+xml;base64,) 99% 50% no-repeat ${theme.colors.white};`,
        WebkitAppearance: 'none',
        MozAppearance: 'none',
        appearance: 'none',
        cursor: 'pointer',
        ':disabled': {
          opacity: 0.5,
          cursor: 'not-allowed'
        },
        '::-ms-expand': {
          display: 'none'
        }
      })
    }
  }
});

export const Option = themed({
  tag: 'option',

  defaultTheme: {
    option: {
      fontSize: 'sm',
      css: {
        fontFamily: 'inherit'
      }
    }
  }
});
