import { baseTheme } from '../baseTheme';
import { getThemeBreakpointNames } from '../getThemeBreakpoints';

describe('getThemeBreakpoints', () => {
  it('when getting breakpoints from base theme', () => {
    const result = getThemeBreakpointNames(baseTheme);
    expect(result).toMatchObject(['xs', 'sm', 'md', 'lg', 'xl']);
  });
});
